import './App.css';
import io from 'socket.io-client';

const socket = io.connect("http://localhost:3001");

function App() {
    const sendMessage = () => {
        socket.emit("send_msg", {message: "wassup"});
    };

    return (
        <div className="App">
            <input type="text" placeholder="Message..."/>
            <button onClick={sendMessage}>Send Message</button>
        </div>
    );    
}

export default App;
