import './App.css';
import io from 'socket.io-client';
import {useState, useEffect} from 'react';

const socket = io(`http://${window.location.hostname}:3001`);

function App() {

    const [ message, setMessage ] = useState("");
    const [ displayMsg, setDisplayMsg ] = useState("");

    const [ roomsList, setRoomsList ] = useState(['yolo', 'room1']);

    // client-to-server emitter
    const sendMessage = () => {
        socket.emit("send_msg", { message });
    };

    // server-to-client listener
    useEffect(() => {
        socket.emit("get_roomsList", (data) => {
            setRoomsList(data);
        })
        socket.on("receive_msg", (data) => {
            setDisplayMsg(data.message);
        });
    }, []);

    return (
        <div className="App">
            <input 
                placeholder="Message..." 
                onChange={(event) => {
                    setMessage(event.target.value);
                }
            }/>
            <button onClick={sendMessage}>Send Message</button>
            <p className="display-message">{displayMsg}</p>
            <h1>ROOMS:</h1>
            <div className="rooms-container">
                {roomsList.map(roomName => {
                    return <p>{roomName}</p>
                })}
            </div>
        </div>
    );    
}

export default App;
